
public class Main02_01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int sum;
		sum = 1;
		System.out.println("1부터 1까지의 합은 "+ sum + "입니다.");
		sum = 1+2;
		System.out.println("1부터 2까지의 합은 "+ sum + "입니다.");
		sum = 1+2+3;
		System.out.println("1부터 3까지의 합은 "+ sum + "입니다.");
		sum = 1+2+3+4;
		System.out.println("1부터 4까지의 합은 "+ sum + "입니다.");
		sum = 1+2+3+4+5;
		System.out.println("1부터 5까지의 합은 "+ sum + "입니다.");
	}

}
